import React, { useState } from 'react';
import {StyleSheet, Text, SafeAreaView, 
        View, ScrollView, TextInput, 
        TouchableOpacity, StatusBar,
        KeyboardAvoidingView, Keyboard } from 'react-native';

import Icon from 'react-native-vector-icons/FontAwesome5';

const Home = ({navigation}) => {  
  const [text, inputText] = useState({ 
    departure: '',
    arrival: '',
    date: '',
  });

  const getInput = (userInput) => {
    return (val) => {
      inputText({ ...text, [userInput]: val });
      console.log(text);
    }
  }
  
  const onSubmit = () => {
    navigation.navigate('Info', { text })
  } 
  
  return (
    <>
        <StatusBar barStyle="light-content" backgroundColor="#00660e" />
        <ScrollView style={styles.scrollView}>
        <SafeAreaView style={styles.container}>
            <KeyboardAvoidingView>
                <View style={styles.main}>
                    <Text style={styles.title}>Boatzy</Text>
                    <Text style={styles.titleForm}>Pelabuhan Awal</Text>
                    <View style={styles.search}>
                    <Icon name="ship" size={20} color='#283593'/>
                    <TextInput
                    style={styles.input}
                    placeholder="Pilih Pelabuhan Awal"
                    onChangeText={getInput('departure')}
                    value={text.departure}
                    />
                    </View>   
                    <Text style={styles.titleForm}>Pelabuhan Tujuan</Text>
                    <View style={styles.search}>
                        <Icon name="ship" size={20} color='#283593' />
                        <TextInput
                        style={styles.input}
                        placeholder="Pilih Pelabuhan Tujuan"
                        onChangeText={getInput('arrival')}
                        value={text.arrival}
                        />
                    </View>  
                    <Text style={styles.titleForm}>Kelas Layanan</Text>
                    <View style={styles.search}>
                        <Icon name="person-seat" size={20} color='#283593' />
                        <TextInput
                        style={styles.input}
                        placeholder="Pilih Layanan"
                        onChangeText={getInput('arrival')}
                        value={text.arrival}
                        />
                    </View>             
                    <Text style={styles.titleForm}>Tanggal Masuk Pelabuhan</Text>
                    <View style={styles.search}>
                        <Icon name="calendar" size={20} color='#283593' />
                        <TextInput
                        style={styles.input}
                        placeholder="Pilih Tanggal Masuk"
                        onChangeText={getInput('date')}
                        value={text.date}
                        />
                    </View>
                    <Text style={styles.titleForm}>Jam Masuk Pelabuhan</Text>
                    <View style={styles.search}>
                        <Icon name="clock" size={20} color='#283593' />
                        <TextInput
                        style={styles.input}
                        placeholder="Pilih Jam Masuk"
                        onChangeText={getInput('date')}
                        value={text.date}
                        />
                    </View>
                    <Text style={styles.titleForm}>Masukkan Jumlah Penumpang</Text>
                    <View style={styles.search}>
                        <Icon name="user" size={20} color='#283593' />
                        <TextInput
                        style={styles.input}
                        placeholder="Pilih Jumlah Penumpang"
                        onChangeText={getInput('date')}
                        value={text.date}
                        />
                    </View>            
                    <TouchableOpacity
                        style={styles.button}
                        onPress={onSubmit}>
                        <Text style={styles.buttonText}><Icon name="search" size={15} marginRight={10} color='#283593' />Cari</Text>
                    </TouchableOpacity>
                </View>
                <Text style={styles.copyright}>Muhammad Nadhif Athalla - 119140209</Text>
            </KeyboardAvoidingView>
        </SafeAreaView>
        </ScrollView>
    </>
  );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    scrollView: {
        backgroundColor: '#00660e',
    },
    title: {
        color: '#000',
        fontSize: 50,
        fontWeight: 'bold',
        letterSpacing: 5,
        textAlign: 'center',
        marginTop: 20,
        paddingBottom: 20    
    },
    main: {
        margin: 40,  
        marginTop: 50,  
        backgroundColor: '#fff',
        borderRadius: 10,
        paddingVertical: 20,
        paddingHorizontal: 10,
        marginVertical: 0,
    },
    search: {
        marginLeft:15,    
        flexDirection: 'row', 
        justifyContent: 'center',   
        alignItems: 'center',
        paddingBottom: 20
    },
    calendarIcon: {
        paddingRight: 5,
    },
    titleForm: {
        marginLeft: 10,
        marginBottom: 5,
        fontSize: 16,
        fontWeight: 'bold',
        color: '#00660e',
    },
    input: {
        borderWidth: 1,
        borderColor: '#00660e',
        borderRadius: 5,
        color: '#00660e',
        marginHorizontal: 10,
        paddingHorizontal: 10,
        paddingVertical: 5,
        height: 40,
        width: 250,
        flex: 1,
    },
    button: {
        backgroundColor: '#eb7900',
        borderRadius: 5,
        marginHorizontal: 10,
        paddingVertical: 5,
        elevation: 2,
    },
    buttonText: {
        color: '#00660e',
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 16,
    },
    copyright: {
        color: '#fff',
        textAlign: 'center',
        marginTop: 50,
    },
});

export default Home;
